import { useState } from "react";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
    const [year, setYear] = useState('2020');
    // const [filteredInfoText, setFilteredInfoText] = useState('2019, 2021 & 2022');
    // let filteredInfoText = '2019, 2021 & 2022';
    // if (year === '2019') {
    //     filteredInfoText = '2020, 2021, 2022';
    // }
    // else if (year === '2021') {
    //     filteredInfoText = '2019, 2020, 2022';
    // }
    // else if (year === '2022'){
    //     filteredInfoText = '2019, 2020, 2021';
    // }

    const getYearHandler = (yearData) => {
        setYear(yearData);
        console.log(yearData);
    }

    console.log(props.inputExpense);

    const expenses = [
        props.inputExpense,
        {
            id: 'e1',
            title: 'Toilet Paper',
            amount: 94.12,
            date: new Date(2020, 7, 14),
        },
        { 
            id: 'e2', 
            title: 'New TV', 
            amount: 799.49, 
            date: new Date(2021, 2, 12) 
        },
        {
            id: 'e3',
            title: 'Car Insurance',
            amount: 294.67,
            date: new Date(2021, 2, 28),
        },
        {
            id: 'e4',
            title: 'New Desk (Wooden)',
            amount: 450,
            date: new Date(2021, 5, 12),
        },
    ];

    return (
        <Card className="expenses">
            <ExpensesFilter 
                getYear = {getYearHandler}
                defaultYear = {year}
            />
            {/* <p style={{color:"white"}}>Data for years {filteredInfoText} is hidden.</p> */}
            {props.inputExpense.title}
            <ExpenseItem 
                title = {expenses[0].title} 
                amount = {expenses[0].amount}
                date = {expenses[0].date}
            />
            <ExpenseItem 
                title = {expenses[1].title} 
                amount = {expenses[1].amount}
                date = {expenses[1].date}
            />
            <ExpenseItem 
                title = {expenses[2].title} 
                amount = {expenses[2].amount}
                date = {expenses[2].date}
            />
            <ExpenseItem 
                title = {expenses[3].title} 
                amount = {expenses[3].amount}
                date = {expenses[3].date}
            />
        </Card>
    )
}

export default Expenses;
