import { useState } from 'react';
import './ExpensesFilter.css';

const ExpensesFilter = () => {
    const [year, setYear] = useState('2022');

    const changeHandler = (e) => {
        setYear(e.target.value);
        console.log(year);
    }

    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select value={year} onChange={changeHandler}>
                    <option 
                        value='2022' 
                        // onChange={(e) => {clickHandler('2022', e.target.value)}}
                    >2022</option>
                    <option 
                        value='2021'
                        // onClick={(e) => {clickHandler('2021', e.target.value)}}
                    >2021</option>
                    <option 
                        value='2020'
                        // onClick={(e) => {clickHandler('2020', e.target.value)}}
                    >2020</option>
                    <option 
                        value='2019'
                        // onClick={(e) => {clickHandler('2019', e.target.value)}}
                    >2019</option>
                </select>
            </div>
        </div>
    );
};

export default ExpensesFilter;
