import { useState } from 'react';
import './NewExpense.css';
import NewExpenseForm from './NewExpenseForm';

const NewExpense = (props) => {
    const saveExpenseDataHandler = (inputExpenseData) => {
        const expenseData = {
            ...inputExpenseData,
            id: Math.random().toString()
        }
        props.onAppExpense(expenseData);
    }

    const expenseForm = () => {
        return <NewExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
    }
    // const defaultDisplay = <button onClick={hello} className='new-expense__actions'>Add New Expense</button>
    const [expenseFormDisplay, setExpenseFormDisplay] = 
        useState(<button onClick={() => expenseForm()} className='new-expense__actions'>Add New Expense</button>);

    // const setDisplay = () => {
    //     setExpenseFormDisplay(expenseForm);
    // }

    return (
        <div className="new-expense">
            {expenseFormDisplay}
            {/* <NewExpenseForm onSaveExpenseData={saveExpenseDataHandler} /> */}
        </div>
    )
}

export default NewExpense;
