import { useState } from 'react';
import './NewExpense.css';
import NewExpenseForm from './NewExpenseForm';

const NewExpense = (props) => {
    const saveExpenseDataHandler = (inputExpenseData) => {
        const expenseData = {
            ...inputExpenseData,
            id: Math.random().toString()
        }
        props.onAppExpense(expenseData);
    }

    const [expenseFormDisplay, setExpenseFormDisplay] = useState();

    const changeDisplay = () => {
        setExpenseFormDisplay(<NewExpenseForm onSaveExpenseData={saveExpenseDataHandler} />)
    }

    const [defaultDisplay, setDefaultDisplay] = useState(<button onClick={changeDisplay} className='new-expense__actions'>Add New Expense</button>);

    return (
        <div className="new-expense">
            {defaultDisplay}
            {/* <NewExpenseForm onSaveExpenseData={saveExpenseDataHandler} /> */}
        </div>
    )
}

export default NewExpense;
